import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Header from "./Header";
import Nav from "./Nav";
import Footer from "./Footer";
import Home from "./Home";
import NewPost from "./NewPost";
import PostPage from "./PostPage";
import About from "./About";
import Missing from "./Missing";
import { useState, useEffect } from "react";
import {format} from 'date-fns';

function App() {
  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "My first post",
      datetime: "Jully 01, 2021 11:17:36 AM",
      body: "Lorem text, lorem text, lorem text, lorem text, lorem text, lorem text, lorem text"
    },
    {
      id: 2,
      title: "My first post",
      datetime: "Jully 01, 2021 11:17:36 AM",
      body: "Lorem text, lorem text, lorem text, lorem text, lorem text, lorem text, lorem text"
    },
    {
      id: 3,
      title: "My first post",
      datetime: "Jully 01, 2021 11:17:36 AM",
      body: "Lorem text, lorem text, lorem text, lorem text, lorem text, lorem text, lorem text"
    },
    {
      id: 4,
      title: "My first post",
      datetime: "Jully 01, 2021 11:17:36 AM",
      body: "Lorem text, lorem text, lorem text, lorem text, lorem text, lorem text, lorem text"
    }
  ]);
  const [search, setSearch] = useState('');
  const [postTitle, setPostTitle] = useState('');
  const [postBody, setPostBody] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSubmit = (e) => {
   e.preventDefault();
   const id=posts.length?(posts[posts.length-1]+1):1;
   const datatime=format(new Date(),'MMMM dd ,YYYY pp');
   const newPost={id,title:postTitle,datatime, body:postBody};
   const allPosts={...posts,newPost};
   setPosts(allPosts)
  };

  const handleDelete = (id) => {
    const postsList = posts.filter(post => post.id !== id);
    console.log(postsList);
    setPosts(postsList);
  };

  return (
    <div className="App">
      <Header title="React JS Blog" />
      <Nav
        search={search}
        setSearch={setSearch}
      />
      <Router>
        <Routes>
          <Route path='/' element={<Home posts={posts} />} />
          <Route path='/post' element={<NewPost
            handleSubmit={handleSubmit}
            postTitle={postTitle}
            setPostTitle={setPostTitle}
            postBody={postBody}
            setPostBody={setPostBody}
          />} />
          <Route path='/post/:id' element={<PostPage posts={posts} setPosts={setPosts} handleDelete={handleDelete} />} />
          <Route path='/about' element={<About />} />
          <Route path='/*' element={<Missing />} />
        </Routes>
      </Router>

      <Footer />
    </div>
  );
}

export default App;
